import { Link } from "react-router-dom";
import io from 'socket.io-client';




const Leaderboard = () => {
    let students = []
    var database_data
    function compare(a, b) {
        if (a.points > b.points) {
            return -1;
        }
        if (a.points < b.points) {
            return 1;
        }
        return 0;

    }
    const socket = io('http://localhost:5000');
    socket.on('connect', (data) => {
        console.log("connected to socket")
        socket.emit('get_database')
    })
    socket.on('all_data', (data) => {
        students = data
        build_page()
    })
    socket.on('update_page', (data) => {
        database_data = data
        database_data.sort(compare)
        students.sort(compare)
        if (database_data.length !== students.length) {
            document.body.innerHTML = ''
            students = database_data
            build_page()
        }
        else {
            students = database_data
            var sum = 0
            for (let i = 0; i < students.length; i++) {
                sum += students[i].points
            }
            var average = sum / students.length
            average = average.toFixed(2)
            document.getElementsByClassName('average_score')[0].innerHTML = 'Average Score: ' + average
            for (let i = 1; i < students.length; i++) {
                document.querySelector("div.leaderboard").getElementsByTagName("div")[i].getElementsByClassName("name")[0].innerHTML = students[i].name
                document.querySelector("div.leaderboard").getElementsByTagName("div")[i].getElementsByClassName("raw_score")[0].innerHTML = students[i].points
                document.querySelector("div.leaderboard").getElementsByTagName("div")[i].getElementsByClassName("actual_score")[0].innerHTML = (students[i].points / average * 100).toFixed(2) + "%"

            }
        }

        build_page()


    })
    document.body.style.background = '#292C2E'


    function build_page() {

        students.sort(compare)
        var sum = 0
        for (let i = 0; i < students.length; i++) {
            sum += students[i].points
        }
        const stats = document.createElement('div')
        stats.style = 'width: 98%; height:' + '10%;border-width:5px; border-style: solid; display: flex; justify-content: left; align-items: center;text-align: center'        
        var statistics_word = document.createElement('p')
        var teacher = document.createElement('p')
        var avg = document.createElement('p')
        var high = document.createElement('p')
        var low = document.createElement('p')
        stats.appendChild(statistics_word)
        stats.appendChild(teacher)
        stats.appendChild(avg)
        stats.appendChild(high)
        stats.appendChild(low)
        document.querySelector('div.leaderboard').appendChild(stats)
        var average = sum / students.length
        average = average.toFixed(2)
        const div = document.createElement('div')
        var name = document.createElement('p')
        var raw_score = document.createElement('p')
        var actual_score = document.createElement('p')
        var average_score = document.createElement('p')
        div.style = 'width: 98%; height:' + '10%;border-width:5px; border-style: solid; display: flex; justify-content: left; align-items: center;text-align: center'
        
        div.style.position = 'absolute'
        div.style.top = 2 + "%";
        div.style.marginLeft = "0.5%"
        div.style.borderRadius = "10px"
        name.innerText = "Name:";
        name.style.fontSize = "6vh"
        name.style.fontFamily = 'Karla'
        name.style.marginLeft = '1%'
        name.style.position = 'absolute'
        raw_score.innerText = "Score:";
        raw_score.style.marginLeft = '50%'
        raw_score.style.position = 'absolute'
        raw_score.style.fontSize = "6vh"
        raw_score.style.fontFamily = 'Karla'
        actual_score.innerText = "Percentage:";
        actual_score.style.position = 'absolute'
        actual_score.style.fontSize = "6vh"
        actual_score.style.fontFamily = 'Karla'
        actual_score.style.marginLeft = '80%'
        average_score.innerText = "Average Score: " + average;
        average_score.style.marginLeft = '20%'
        average_score.style.position = 'absolute'
        average_score.style.fontSize = "6vh"
        average_score.style.fontFamily = 'Karla'
        average_score.className = 'average_score'
        div.style.background = '#6c6f72'
        div.appendChild(name)
        div.append(average_score)
        div.appendChild(raw_score)
        div.appendChild(actual_score)
        document.querySelector('div.leaderboard').appendChild(div)
        
        for (let i = 0; i < students.length; i++) {
            try {
                const div = document.createElement('div')
                var name = document.createElement('p')
                var raw_score = document.createElement('p')
                var actual_score = document.createElement('p')
                div.style = 'background-color:white;width: 98%; height:' + '10%;border-width:5px; border-style: solid; display: flex; justify-content: left; align-items: center;text-align: center'
                div.style.position = 'absolute'
                div.style.top = 13 * (i + 1) + 5 + "%";
                div.style.marginLeft = "0.5%"
                div.style.borderRadius = "10px"
                div.className = students[i].name
                name.innerText = students[i].name;
                name.style.fontSize = "6vh"
                name.style.fontFamily = 'Karla'
                name.style.marginLeft = '1%'
                name.style.position = 'absolute'
                name.className = "name"
                raw_score.innerText = students[i].points;
                raw_score.style.marginLeft = '50%'
                raw_score.style.position = 'absolute'
                raw_score.style.fontSize = "6vh"
                raw_score.style.fontFamily = 'Karla'
                raw_score.className = "raw_score"
                actual_score.innerText = (students[i].points / average * 100).toFixed(2) + '%'
                actual_score.style.position = 'absolute'
                actual_score.style.fontSize = "6vh"
                actual_score.style.fontFamily = 'Karla'
                actual_score.style.marginLeft = '80%'
                actual_score.className = "actual_score"
                div.style.background = '#6c6f72'

                div.appendChild(name)
                div.appendChild(raw_score)
                div.appendChild(actual_score)
                document.querySelector('div.leaderboard').appendChild(div)

            }
            catch {

            }
        }
    }

    return (
        <div>
            <Link style={{ marginLeft: 100 }} to="/">home</Link>
            <Link style={{ marginLeft: 100 }} to="/recorder">recorder</Link>
            <Link style={{ marginLeft: 100 }} to="/leaderboard">leaderboard</Link>
            <div className="leaderboard" style={{ minWidth: "100vh", minHeight: "95vh", background: "#292C2E" }}>
            </div>
        </div>
    )
}

export default Leaderboard
